/**
 * @description
 * Handles all actions and provides the basic APIs to handle actions in infra
 */
declare class ActionsHandler {
    constructor();
    /**
     * Returns the registered handler
     * @param {*} actionName Name of the action
     */
    getHandler(actionName: string | number): any;
    /**
     * Registers the actionHandler
     * @param {*} actionName actionName like showHarness etc.,
     * @param {*} handler Callback handler.
     */
    registerHandler(actionName: string | number, handler: any): void;
}
declare const actionsHandler: ActionsHandler;
export default actionsHandler;
