export type Options = {
    context?: string;
    pageReference?: string;
    containerName?: string;
    containerItemID?: string;
    parentPageReference?: string;
    hasForm?: boolean;
    target?: string;
    viewName?: string;
    referenceList?: string;
    isInsideList?: boolean;
    localeReference?: string;
    readOnly?: boolean;
    inheritedConfig?: any;
    isBulkAction?: boolean;
    containerItemName?: string;
};
export type Meta = {
    type?: string;
    config?: ComponentMetadataConfig;
    options?: Options;
    repeat?: boolean;
    'repeat-self'?: boolean;
    context?: string;
    pageReference?: string;
    children?: any;
};
export type Config = {
    meta?: Meta;
    isRoot?: boolean;
    options?: Options;
    useCustomContext?: boolean;
};
export type ComponentMetadataConfig = {
    readOnly?: boolean;
    inheritedProps?: any;
    contextName?: any;
    visibility?: boolean;
    validatetype?: string;
};
