/**
 * Use these utility APIs to automatically obtain the active context for the currently opened container and act on the context
 */
declare class ActiveContext {
    /**
     * Returns the information of the specified header from the active context
     * <br>
     *
     * @example <caption>In this example, the API returns an object containing the information in the debugInfo header</caption>
     * PCore.getContainerUtils().getActiveContext().getCoreheaders('debugInfo')
     *
     * @param {string} headerName - Name of the header to be retreived from the redux store eg. debugInfo
     * <br><br>
     * @returns {object} returns - An object containing passed header information. If the specified header does not exist or
     * if it doesn't contain any information, undefined will be returned.
     * <br><br>
     */
    getCoreheaders(headerName: any): any;
}
declare const _default: ActiveContext;
export default _default;
